mapper.select:
select count(*) cnt from items
where id like '%'||#{searchValue}||'%'
where name like '%'||#{searchValue}||'%'
where amount >= #{searchValue}
where amount <= #{searchValue}
where buyPrice >== #{searchValue}
where buyPrice <= #{searchValue}
where memo like '%'||#{searchValue}||'%'
where (select name from employee emp, general_emplyee gemp where emp.id-gemp.id) like '%'||#{searchValue}||'%'

select
*
from (
select a.*, rownum as r_num from
(
select id, category,
name, amount, buyDate, buyPrice, manager, memo
from items




			where id like '%'||#{searchValue}||'%'


			where name like '%'||#{searchValue}||'%'


			where amount >=
			#{searchValue}


			where amount <=
			#{searchValue}


			where buyPrice >==
			#{searchValue}


			where buyPrice <=
			#{searchValue}


			where memo like '%'||#{searchValue}||'%'


			where (select name from employee emp,
			general_emplyee gemp where emp.id-gemp.id) like
			'%'||#{searchValue}||'%'






) a
)
where r_num between #{begin} and #{end}
