mapper.select:
select * from ( select a.*, rownum as r_num from ( select emp.name name, emp.position position, emp.tel tel, dept.name dept_name from employee emp, (select name, id from department) dept where emp.dept_id = dept.id
and emp.name LIKE '%'||#{searchValue}||'%'
and emp.position LIKE '%'||#{searchValue}||'%'
and dept.name LIKE '%'||#{searchValue}||'%'
) a ) where r_num between #{begin} and #{end}

